import tkinter as tk
from tkinter import ttk
from ttkwidgets.autocomplete import AutocompleteCombobox
from PIL import Image, ImageTk
import requests
from io import BytesIO
from functools import partial
from ActorApi import *
import ApiKey


class ActorSearchPage(tk.Frame):

    def __init__(self, parent, controller):
        tk.Frame.__init__(self, parent)
        self.controller = controller



        titleLabel = tk.Label(self, text="Movie Search Engine", font=("Greylock", 50))
        titleLabel.pack(side=tk.TOP, anchor=tk.NW, padx=20, pady=5)

        functionFrame = tk.Frame(self)
        self.controller.values["function"].set(1)
        rb1 = tk.Radiobutton(functionFrame, text="Actor", font=("Calibri", 15),
                             variable=self.controller.values["function"], value=1)
        rb2 = tk.Radiobutton(functionFrame, text="Movie", font=("Calibri", 15),
                             variable=self.controller.values["function"], value=2)
        rb3 = tk.Radiobutton(functionFrame, text="Collaboration", font=("Calibri", 15),
                             variable=self.controller.values["function"], value=3)
        rb1.pack(side=tk.LEFT)
        rb2.pack(side=tk.LEFT, padx=10)
        rb3.pack(side=tk.LEFT, padx=10)
        functionFrame.pack(side=tk.TOP, anchor=tk.NW, padx=20)

        searchLTFrame = tk.Frame(self)
        suggestions = ["Tom Hardy", "Tom Curise", "Tom Holland"]
        queryEntry = AutocompleteCombobox(searchLTFrame, width=40, font=("Calibri", 20),
                                          completevalues=suggestions,
                                          textvariable=self.controller.values["query"])
        queryEntry.pack(side=tk.LEFT)
        searchButton = tk.Button(searchLTFrame, font=("Calibri", 20), text="Search",
                                 height=1, bd=1, command=self.search)

        homeButton = tk.Button(searchLTFrame, font=("Calibri", 20), text="Home", height=1, bd=1,
                               command=self.home)
        homeButton.pack(side=tk.RIGHT)
        searchButton.pack(side=tk.RIGHT, padx=10)
        searchLTFrame.pack(side=tk.TOP, anchor=tk.NW, padx=20)


    def urltoImg(self, url, w, h):
        response = requests.get(url)
        image = ImageTk.PhotoImage(Image.open(BytesIO(response.content)).resize((w, h)))
        return image

    def _on_mousewheel(self, event):
        self.canvas.yview_scroll(-1 * (event.delta // 120), "units")

    def update(self):
        self.frame = tk.Frame(self)
        self.frame.pack(side=tk.TOP, anchor=tk.NW, padx=20, pady=10, fill=tk.BOTH, expand=1)
        self.canvas = tk.Canvas(self.frame)
        self.canvas.pack(side=tk.LEFT, fill=tk.BOTH, expand=1)
        self.scrollbar = ttk.Scrollbar(self.frame, orient=tk.VERTICAL, command=self.canvas.yview)
        self.scrollbar.pack(side=tk.RIGHT, fill=tk.Y)
        self.canvas.configure(yscrollcommand=self.scrollbar.set)
        self.canvas.bind_all("<MouseWheel>", self._on_mousewheel)
        self.canvas.bind('<Configure>', lambda e: self.canvas.configure(scrollregion=self.canvas.bbox("all")))
        self.content = tk.Frame(self.canvas)
        self.canvas.create_window((0, 0), window=self.content, anchor="nw")

        # self.outcomeFrame = tk.Frame(self.content)
        # self.outcomeFrame.pack(side=tk.TOP, anchor=tk.NW)

        self.outcomeLabel = tk.Label(self.content, text="Search Results", font=("Calibri", 30))
        self.outcomeLabel.grid(row=0, column=0)


        # key = "d059029339msh401f1d3ff774d0cp13c27ejsn70e16e2f94b2"
        key = ApiKey.give_key()
        actor_list = ActorApi(key)
        self.controller.values["result"] = actor_list.get_bio(self.controller.values["query"].get())
        # self.controller.values["result"] = [{'id': 'nm4043618', 'name': 'Tom Holland', 'birthDate': '1996-06-01', 'gender': 'male', 'height': 173.0, 'fullName': 'Thomas Stanley Holland', 'image': 'https://m.media-amazon.com/images/M/MV5BNzZiNTEyNTItYjNhMS00YjI2LWIwMWQtZmYwYTRlNjMyZTJjXkEyXkFqcGdeQXVyMTExNzQzMDE0._V1_.jpg', 'akas': None, 'nicknames': None, 'birthPlace': 'Kingston upon Thames, England, UK', 'spouses': None, 'trademarks': ['Mostly portrays inexperienced and enthusiastic kids', 'Solid American accent', 'Youthful face and voice'], 'introduction': 'Thomas Stanley Holland was born in Kingston-upon-Thames, Surrey, to Nicola Elizabeth (Frost), a photographer, and Dominic Holland (Dominic Anthony Holland), who is a comedian and author. His paternal grandparents were from the Isle of Man and Ireland, respectively. He lives with his parents and three younger brothers - Paddy and twins Sam and Harry. Tom attended Donhead Prep School. Then, after a successful eleven plus exam, he became a pupil at Wimbledon College. Having successfully completed his GCSEs, in September 2012 Tom started a two-year course in the BRIT School for Performing Arts & Technology notable for its numerous famous alumni. ...', 'full_intruduction': 'Thomas Stanley Holland was born in Kingston-upon-Thames, Surrey, to Nicola Elizabeth (Frost), a photographer, and Dominic Holland (Dominic Anthony Holland), who is a comedian and author. His paternal grandparents were from the Isle of Man and Ireland, respectively. He lives with his parents and three younger brothers - Paddy and twins Sam and Harry. Tom attended Donhead Prep School. Then, after a successful eleven plus exam, he became a pupil at Wimbledon College. Having successfully completed his GCSEs, in September 2012 Tom started a two-year course in the BRIT School for Performing Arts & Technology notable for its numerous famous alumni.\n\nHolland began dancing at a hip hop class at Nifty Feet Dance School in Wimbledon, London. His potential was spotted by choreographer Lynne Page (who was an Associate to Peter Darling, choreographer of Billy Elliot and Billy Elliot the Musical) when he performed with his dance school as part of the Richmond Dance Festival 2006. After eight auditions and subsequent two years of training, on 28 June 2008 Tom made his West End debut in Billy Elliot the Musical as Michael, Billy\'s best friend. He gave his first performance in the title role of Billy on 8 September 2008 getting rave reviews praising his versatile acting and dancing skills.\n\nIn September 2008 Tom (together with co-star Tanner Pflueger) appeared on the news programme on channel FIVE and gave his first TV interview. In 2009 Tom was featured on ITV1 show "The Feel Good Factor". At the launch show on 31 January he and two other Billy Elliots, Tanner Pflueger and Layton Williams, performed a specially choreographered version of Angry Dance from Billy Elliot the Musical, after which Tom was interviewed by host Myleene Klass. Then he became involved into training five ordinary British schoolboys learning to get fit and preparing their dance routine (fronted by Tom) for the final "The Feel Good Factor" show on 28 March 2009. On 11 March 2010, Tom, along with fellow Billy Elliots Dean-Charles Chapman and Fox Jackson-Keen appeared on The Alan Titchmarsh Show on ITV1.\n\nOn 8 March 2010, to mark the fifth anniversary of Billy Elliot the Musical, four current Billy Elliots, including Tom Holland, were invited to 10 Downing Street to meet the Prime Minister Gordon Brown. It was Tom Holland who was chosen to be a lead at the special fifth anniversary show on 31 March 2010. Elton John, Billy Elliot the Musical composer, who was at the audience, called Tom\'s performance "astonishing" and said that he was "blown away" by it. Holland had been appearing on a regular basis as Billy in Billy Elliot the Musical rotating with three other performers till 29 May 2010 when he finished his run in the musical.\n\nIn two months after leaving Billy Elliot the Musical, Holland successfully auditioned for a starring role in the film The Impossible (directed by Juan Antonio Bayona) alongside Naomi Watts and Ewan McGregor. The Impossible was based on a true story that took place during the 2004 Indian Ocean earthquake. The film premiered at the Toronto International Film Festival on September 9, 2012, and was released in Europe in October 2012, and in North America in December 2012.\n\nTom has received universal praise for his performance, in particular: "What a debut, too, from Tom Holland as the eldest of their three lads" (The Telegraph); "Tom Holland, making one of the finest feature debuts in years" (HeyUGuys); "the excellent Tom Holland" (The Guardian); "The child performers are uncanny and there is an especially terrific performance from Tom Holland as the resourceful, levelheaded Lucas terrified but tenacious in the face of an unspeakable ordeal" (Screen Daily); "Young Holland in particular is astonishingly good as the terrified but courageous Lucas." (The Hollywood Reporter); "However, the real acting standout in The Impossible is the performance of Tom Holland as the eldest son Lucas. His portrayal is genuine, and at no moment does it feel melodramatic and forced. The majority of his scenes are separate from the lead actors and for the most part it feels like The Impossible is Holland\'s film" (Entertainment Maven); "Mr. Holland, meanwhile, matures before our eyes, navigating the passage from adolescent self-absorption to profound and terrible responsibility. He is a terrific young actor" (New York Times).\n\nTom has given a number of interviews about his role in The Impossible. In particular, he talked on video to Vanity Fair Senior West Coast editor Krista Smith and with IAMROGUE\'s Managing Editor Jami Philbrick. He has also given interviews to The Hollywood Reporter, to the MovieWeb, to Today Show on NBC and to other outlets. Tom\'s director and co-stars have also talked about him. Juan Antonio Bayona: "He had this extraordinary ability to get into the emotion and portray it in a very, very easy way. The best I\'d ever seen in a kid." Ewan McGregor: "It was wonderful watching Tom who had never worked in front of a camera before, to see him really get it and grow as a film actor as he went along. He\'s really talented and polite to everyone. It\'s very easy for children to lose perspective but he\'s absolutely on the right road and a brilliant actor." Naomi Watts: "He has an incredible emotional instrument and an unbelievable sense of himself... Tom Holland and I had a couple of moments where we came together and I could just tell how wonderful he was and what a beautiful instrument he had. It was just easy to work with him, that was one of the greatest highlights for me: discovering a friendship with Tom off-screen and this beautiful relationship between mother and son on-screen. The intimacy that develops through the course of the film between Lucas and Maria, I just loved that relationship. I mean, Tom is a beyond gifted actor. He\'s just a raw, open talent that is just so easy to work with. And Tom, he\'s inspiring, he kind of lifts everyone\'s game around him because he can do nothing but tell the truth. He was great."\n\nIn his turn, Tom Holland has returned favours to Naomi Watts when he was asked to present Desert Palm Achievement Award to her at Palm Springs International Film Festival. According to HitFix: "One recurring theme of the night was how the introductions were often better than actual winner\'s speeches... The best intro, however, had to go to 16-year-old Tom Holland who intro\'d his "Impossible" co-star Watts. Holland admitted of all of Watts\' great performances his dad had only let him see "King Kong" and while they spent six weeks shooting in a water tank he didn\'t know it was "difficult" because he actually "loved it"... Most important, this was Holland\'s first film role and he sweetly noted, "From the moment I met you, you took my hand and you never let go." Cue the "awwww" from the audience." The presentation is available on video.\n\nIn 2011, Holland was cast in British version of the animation film Arrietty, produced by Japan\'s cult Studio Ghibli. He has provided voice over for the principal character Sho. In 2012 Tom Holland played the starring role of Isaac in the film "How I lived Now", (directed by Kevin Macdonald) alongside Saoirse Ronan. The film was released in 2013.\n\nOn 17 October 2012, Holland became a recipient of Hollywood Spotlight Award for his role in The Impossible. "We are very excited that we will be able to recognize acting talents that are on the road to discovery and stardom," said Carlos de Abreu, founder and executive director of the Hollywood Film Awards in a statement. On 6 December 2012 it was announced that Holland became a winner of the National Board of Review award in the "Breakthrough Actor" category. In the end of December 2012, Holland was voted a winner for the year\'s Best Youth Performance in Nevada Critics Awards.\n\nIn December 2012, Holland received a number of nominations for his role in The Impossible: for the 18th annual Critics\' Choice Movie Awards, in the "Best Young Acror/Acress" category; for Chicago Film Critics Association Awards 2012 in the "Most Promising Performer" category; for the 27th Goya Awards in the "Best New Actor" category; for the Washington D.C. Area Film Critics Association Awards 2012 in the "Best Youth Performance" category; for the London Film Critics Circle Awards 2012 in the "Young British Performer of the Year" category.\n\nKristopher Tapley, Editor-at-Large of HitFix, reported on 27 August 2012 that Summit Entertainment, the company responsible for distribution of The Impossible in USA, would be campaigning Holland rather than McGregor as the lead, and strongly argued that Tom Holland deserved to be nominated for an Academy Award in the Best Actor category. The fact of long-listing for an Academy Award was confirmed in the article in the Hollywood Reporter: "And though McGregor stars as his father in the film, Holland has been submitted as the lead actor for awards consideration. Regardless if he receives any nominations, his performance as the strong-willed and determined eldest son is garnering critical acclaim."\n\nAs one of the most promising young actors, Holland was featured in Screen International\'s "UK Stars of Tomorrow - 2012" and in Variety\'s "Youth Impact Report 2012". Holland has been signed up by William Morris Endeavor (WME) global talent agency and is represented by Curtis Brown literary and talent agency.\n\nIn 2015, Tom was cast as Peter Parker/Spider-Man in Sony and Marvel\'s films. He has played the role in Captain America: Civil War (2016), Spider-Man: Homecoming (2017), and Avengers: Infinity War (2018).', 'known_for': [{'title': 'Spider-Man: Homecoming', 'image': 'https://m.media-amazon.com/images/M/MV5BNTk4ODQ1MzgzNl5BMl5BanBnXkFtZTgwMTMyMzM4MTI@._V1_.jpg'}, {'title': 'Spider-Man: Far from Home', 'image': 'https://m.media-amazon.com/images/M/MV5BMGZlNTY1ZWUtYTMzNC00ZjUyLWE0MjQtMTMxN2E3ODYxMWVmXkEyXkFqcGdeQXVyMDM2NDM2MQ@@._V1_.jpg'}, {'title': 'Captain America: Civil War', 'image': 'https://m.media-amazon.com/images/M/MV5BMjQ0MTgyNjAxMV5BMl5BanBnXkFtZTgwNjUzMDkyODE@._V1_.jpg'}, {'title': 'The Impossible', 'image': 'https://m.media-amazon.com/images/M/MV5BMjA5NTA3NzQ5Nl5BMl5BanBnXkFtZTcwOTYxNjY0OA@@._V1_.jpg'}]}, {'id': 'nm0000158', 'name': 'Tom Hanks', 'birthDate': '1956-07-09', 'gender': 'male', 'height': 182.88, 'fullName': 'Thomas Jeffrey Hanks', 'image': 'https://m.media-amazon.com/images/M/MV5BMTQ2MjMwNDA3Nl5BMl5BanBnXkFtZTcwMTA2NDY3NQ@@._V1_.jpg', 'akas': ['Tom H.', 'Tomu Hankusu'], 'nicknames': None, 'birthPlace': 'Concord, California, USA', 'spouses': [{'attributes': '(2 children)', 'current': True, 'fromDate': '1988-04-30', 'id': '/name/nm0001854/', 'name': 'Rita Wilson'}, {'attributes': '(divorced) (2 children)', 'fromDate': '1978-01-24', 'id': '/name/nm2225002/', 'name': 'Samantha Lewes', 'toDate': '1987-03-19'}], 'trademarks': ['Frequently plays ordinary characters in extraordinary situations', 'Likable, mild-mannered and good-natured personality', 'Frequently works with Robert Zemeckis, Steven Spielberg, and Ron Howard'], 'introduction': 'Thomas Jeffrey Hanks was born in Concord, California, to Janet Marylyn (Frager), a hospital worker, and Amos Mefford Hanks, an itinerant cook. His mother\'s family, originally surnamed "Fraga", was entirely Portuguese, while his father was of mostly English ancestry. Tom grew up in what he has called a "fractured" family. He moved around a great deal after his parents\' divorce, living with a succession of step-families. No problems, no alcoholism - just a confused childhood. He has no acting experience in college and credits the fact that he could not get cast in a college play with actually starting his career. He went downtown, and auditioned for a community theater play, was invited by the director of that play to go to Cleveland, and there his acting career started. ...', 'full_intruduction': 'Thomas Jeffrey Hanks was born in Concord, California, to Janet Marylyn (Frager), a hospital worker, and Amos Mefford Hanks, an itinerant cook. His mother\'s family, originally surnamed "Fraga", was entirely Portuguese, while his father was of mostly English ancestry. Tom grew up in what he has called a "fractured" family. He moved around a great deal after his parents\' divorce, living with a succession of step-families. No problems, no alcoholism - just a confused childhood. He has no acting experience in college and credits the fact that he could not get cast in a college play with actually starting his career. He went downtown, and auditioned for a community theater play, was invited by the director of that play to go to Cleveland, and there his acting career started.\n\nRon Howard was working on Splash (1984), a fantasy-comedy about a mermaid who falls in love with a business executive. Howard considered Hanks for the role of the main character\'s wisecracking brother, which eventually went to John Candy. Instead, Hanks landed the lead role and the film went on to become a surprise box office success, grossing more than $69 million. After several flops and a moderate success with the comedy Dragnet (1987), Hanks\' stature in the film industry rose. The broad success with the fantasy-comedy Big (1988) established him as a major Hollywood talent, both as a box office draw and within the film industry as an actor. For his performance in the film, Hanks earned his first Academy Award nomination as Best Actor.\n\nHanks climbed back to the top again with his portrayal of a washed-up baseball legend turned manager in A League of Their Own (1992). Hanks has stated that his acting in earlier roles was not great, but that he subsequently improved. In an interview with Vanity Fair, Hanks noted his "modern era of movie making ... because enough self-discovery has gone on ... My work has become less pretentiously fake and over the top". This "modern era" began for Hanks, first with Sleepless in Seattle (1993) and then with Philadelphia (1993). The former was a blockbuster success about a widower who finds true love over the radio airwaves. Richard Schickel of Time magazine called his performance "charming", and most critics agreed that Hanks\' portrayal ensured him a place among the premier romantic-comedy stars of his generation.\n\nIn Philadelphia, he played a gay lawyer with AIDS who sues his firm for discrimination. Hanks lost 35 pounds and thinned his hair in order to appear sickly for the role. In a review for People, Leah Rozen stated, "Above all, credit for Philadelphia\'s success belongs to Hanks, who makes sure that he plays a character, not a saint. He is flat-out terrific, giving a deeply felt, carefully nuanced performance that deserves an Oscar." Hanks won the 1993 Academy Award for Best Actor for his role in Philadelphia. During his acceptance speech, he revealed that his high school drama teacher Rawley Farnsworth and former classmate John Gilkerson, two people with whom he was close, were gay.\n\nHanks followed Philadelphia with the blockbuster Forrest Gump (1994) which grossed a worldwide total of over $600 million at the box office. Hanks remarked: "When I read the script for Gump, I saw it as one of those kind of grand, hopeful movies that the audience can go to and feel ... some hope for their lot and their position in life ... I got that from the movies a hundred million times when I was a kid. I still do." Hanks won his second Best Actor Academy Award for his role in Forrest Gump, becoming only the second actor to have accomplished the feat of winning consecutive Best Actor Oscars.\n\nHanks\' next role - astronaut and commander Jim Lovell, in the docudrama Apollo 13 (1995) - reunited him with Ron Howard. Critics generally applauded the film and the performances of the entire cast, which included actors Kevin Bacon, Bill Paxton, Gary Sinise, Ed Harris, and Kathleen Quinlan. The movie also earned nine Academy Award nominations, winning two. Later that year, Hanks starred in Disney/Pixar\'s computer-animated film Toy Story (1995), as the voice of Sheriff Woody. A year later, he made his directing debut with the musical comedy That Thing You Do! (1996) about the rise and fall of a 1960s pop group, also playing the role of a music producer.', 'known_for': [{'title': 'Cast Away', 'image': 'https://m.media-amazon.com/images/M/MV5BN2Y5ZTU4YjctMDRmMC00MTg4LWE1M2MtMjk4MzVmOTE4YjkzXkEyXkFqcGdeQXVyNTc1NTQxODI@._V1_.jpg'}, {'title': 'Big', 'image': 'https://m.media-amazon.com/images/M/MV5BMDQ1ODM5MTMtMjAwYi00ZGUxLTliNTMtN2ZhODAwMjVhMTRlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_.jpg'}, {'title': 'Forrest Gump', 'image': 'https://m.media-amazon.com/images/M/MV5BNWIwODRlZTUtY2U3ZS00Yzg1LWJhNzYtMmZiYmEyNmU1NjMzXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_.jpg'}, {'title': 'Apollo 13', 'image': 'https://m.media-amazon.com/images/M/MV5BNjEzYjJmNzgtNDkwNy00MTQ4LTlmMWMtNzA4YjE2NjI0ZDg4XkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_.jpg'}]}, {'id': 'nm0362766', 'name': 'Tom Hardy', 'birthDate': '1977-09-15', 'gender': 'male', 'height': 175.26, 'fullName': 'Edward Thomas Hardy', 'image': 'https://m.media-amazon.com/images/M/MV5BMTQ3ODEyNjA4Nl5BMl5BanBnXkFtZTgwMTE4ODMyMjE@._V1_.jpg', 'akas': ['Thomas Hardy'], 'nicknames': None, 'birthPlace': 'Hammersmith, London, England, UK', 'spouses': [{'attributes': '(2 children)', 'current': True, 'fromDate': '2014-07-04', 'id': '/name/nm2731660/', 'name': 'Charlotte Riley'}, {'attributes': '(divorced)', 'fromDate': '1999', 'name': 'Sarah Ward', 'toDate': '2004'}], 'trademarks': ['Deep raspy voice', 'Muscular physique', 'Extreme changes of physical appearance', 'Full lips and blue-green eyes', 'Frequently features his face partially covered', 'Frequently works with director Christopher Nolan'], 'introduction': "With his breakthrough performance as Eames in Christopher Nolan's sci-fi thriller Inception (2010), English actor Tom Hardy has been brought to the attention of mainstream audiences worldwide. However, the versatile actor has been steadily working on both stage and screen since his television debut in the miniseries Band of Brothers (2001). After being cast in the World War II drama, Hardy left his studies at the prestigious Drama Centre in London and was subsequently cast as Twombly in Ridley Scott's Black Hawk Down (2001) and as the villain Shinzon in Star Trek: Nemesis (2002). ...", 'full_intruduction': 'With his breakthrough performance as Eames in Christopher Nolan\'s sci-fi thriller Inception (2010), English actor Tom Hardy has been brought to the attention of mainstream audiences worldwide. However, the versatile actor has been steadily working on both stage and screen since his television debut in the miniseries Band of Brothers (2001). After being cast in the World War II drama, Hardy left his studies at the prestigious Drama Centre in London and was subsequently cast as Twombly in Ridley Scott\'s Black Hawk Down (2001) and as the villain Shinzon in Star Trek: Nemesis (2002).\n\nEdward Thomas Hardy was born on September 15, 1977 in Hammersmith, London; his mother, Elizabeth Anne (Barrett), is an artist and painter, and his father, Chips Hardy, is a writer. He is of English and Irish descent. Hardy was brought up in East Sheen, London, and first studied at Reed\'s School. His education continued at Tower House School, then at Richmond Drama School, and subsequently at the Drama Centre London, along with fellow Oscar nominee Michael Fassbender. After winning a modeling competition at age 21, he had a brief contract with the agency Models One.\n\nTom spent his teens and early twenties battling delinquency, alcoholism and drug addiction; after completing his work on Star Trek: Nemesis (2002), he sought treatment and has also admitted that his battles with addiction ended his five-year marriage to Sarah Ward. Returning to work in 2003, Hardy was awarded the Evening Standard Most Promising Newcomer Award for his theatre performances in the productions of "In Arabia, We\'d All Be Kings" and "Blood". In 2003, Tom also co-starred in the play "The Modernists" with Paul Popplewell, Jesse Spencer and Orlando Wells.\n\nDuring the next five years, Hardy worked consistently in film, television and theatre, playing roles as varied as Robert Dudley in the BBC\'s The Virgin Queen (2006), Bill Sikes in Oliver Twist (2007) and starring in "The Man of Mode" at the National Theatre. On the silver screen, he appeared in the crime thriller Layer Cake (2004) with Daniel Craig, Sofia Coppola\'s Marie Antoinette (2006), and the romp Scenes of a Sexual Nature (2006).\n\nIn 2006, Hardy created "Shotgun", an underground theatre company along with director Robert Delamere, and directed a play, penned by his father for the company, called "Blue on Blue". In 2007, Hardy received a best actor BAFTA nomination for his touching performance as Stuart Shorter in the BBC adaptation of Alexander Masters\' bestselling biography Stuart: A Life Backwards (2007). Hailed for his transformative character acting, Hardy was lauded for his emotionally and physically convincing portrayal in the ill-fated and warmhearted tale of Shorter, a homeless and occasionally violent man suffering from addiction and muscular dystrophy.\n\nThe following year, he appeared as gay hoodlum Handsome Bob in the Guy Ritchie film RocknRolla (2008), but this would be his next transformation that would prove his extensive range and stun critics. In the film Bronson (2008), Hardy played the notorious Charles Bronson (given name, Michael Peterson), the "most violent prisoner in Britain". Bald, pumped-up, and outfitted with Bronson\'s signature strongman mustache, Hardy is unrecognizable and gives a harrowing performance that is physically fearless and psychologically unsettling. Director Nicolas Winding Refn breaks the fourth wall with Hardy retelling his tales directly to viewers as well as performing them outright before an audience of his own imagining. The performance mixes terrifying brutality, vaudevillian showmanship, wry humor, and an alarming amount of commitment, and won Hardy a British Independent Film Award for Best Actor. The performance got Hollywood\'s attention, and in 2009, Hardy was named one of Variety\'s "10 Actors to Watch". That year, he continued to garner praise for his starring role in The Take (2009), a four-part adaptation of Martina Cole\'s bestselling crime novel, as well as for his performance as Heathcliff in a version of Wuthering Heights (2009).\n\nRecent work includes the aforementioned breakthrough appearance in Inception (2010) alongside Leonardo DiCaprio, Joseph Gordon-Levitt, Cillian Murphy, Tom Berenger, Ken Watanabe, Michael Caine, Marion Cotillard and Elliot Page. The movie was released in July 2010 and became one of top 25 highest grossing films of all time, collecting eight Oscar nominations (including Best Picture) and winning four.\n\nOther films include Warrior (2011), opposite Joel Edgerton, the story of two estranged brothers facing the fight of a lifetime from director Gavin O\'Connor, and This Means War (2012), directed by McG and co-starring Reese Witherspoon and Chris Pine. Tom also starred in the heralded Cold War thriller, Tinker Tailor Soldier Spy (2011) with Colin Firth and Gary Oldman. Hardy rejoined Christopher Nolan for The Dark Knight Rises (2012); he played the villain role of Bane opposite Christian Bale, Anne Hathaway, Joseph Gordon-Levitt and Gary Oldman. Hardy\'s menacing physique and his character\'s scrambled, hard-to-distinguish voice became a major discussion point as the film was released.\n\nOutside of performing, Hardy is the patron for the charity "Flack", which is an organization to aid the recovery of the homeless in Cambridge. And in 2010, Hardy was named an Ambassador for The Prince\'s Trust, which helps disadvantaged youth. On the recent stage, he starred in the Brett C. Leonard play "The Long Red Road" in early 2010. Written for Hardy and directed by Philip Seymour Hoffman, the play was staged at Chicago\'s Goodman Theater.\n\nIn 2015, Hardy starred as the iconic Mad Max in George Miller\'s reboot of his franchise, Mad Max: Fury Road (2015). He also collected a British Independent Film Award for his portrayal of both the Kray twins, Ronnie and Reggie, in Legend (2015), and an Oscar nomination for Best Supporting Actor for his role as John Fitzgerald in The Revenant (2015). Hardy also starred on the BBC series Peaky Blinders (2013), alongside Cillian Murphy, and on the television series Taboo (2017), both created by Steven Knight.\n\nHe has an outlaw biker story among other projects in development. In 2010, Hardy became engaged to fellow English actress Charlotte Riley, whom he starred with in The Take (2009) and Wuthering Heights (2009), and is raising a young son, Louis Thomas Hardy, with ex-girlfriend Rachael Speed. He was appointed Commander of the Order of the British Empire at the 2018 Queen\'s Birthday Honours for his services to drama.', 'known_for': [{'title': 'Inception', 'image': 'https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg'}, {'title': 'The Dark Knight Rises', 'image': 'https://m.media-amazon.com/images/M/MV5BMTk4ODQzNDY3Ml5BMl5BanBnXkFtZTcwODA0NTM4Nw@@._V1_.jpg'}, {'title': 'Locke', 'image': 'https://m.media-amazon.com/images/M/MV5BMTQ1MjE5MzU2M15BMl5BanBnXkFtZTgwNzE4OTMzMTE@._V1_.jpg'}, {'title': 'Mad Max: Fury Road', 'image': 'https://m.media-amazon.com/images/M/MV5BN2EwM2I5OWMtMGQyMi00Zjg1LWJkNTctZTdjYTA4OGUwZjMyXkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_.jpg'}]}, {'id': 'nm0000129', 'name': 'Tom Cruise', 'birthDate': '1962-07-03', 'gender': 'male', 'height': 170.18, 'fullName': 'Thomas Cruise Mapother IV', 'image': 'https://m.media-amazon.com/images/M/MV5BMTk1MjM3NTU5M15BMl5BanBnXkFtZTcwMTMyMjAyMg@@._V1_.jpg', 'akas': ['Tomu Kur√ªzu'], 'nicknames': ['TC'], 'birthPlace': 'Syracuse, New York, USA', 'spouses': [{'attributes': '(divorced) (1 child)', 'fromDate': '2006-11-18', 'id': '/name/nm0005017/', 'name': 'Katie Holmes', 'toDate': '2012-08-20'}, {'attributes': '(divorced) (2 children)', 'fromDate': '1990-12-24', 'id': '/name/nm0000173/', 'name': 'Nicole Kidman', 'toDate': '2001-08-08'}, {'attributes': '(divorced)', 'fromDate': '1987-05-09', 'id': '/name/nm0000211/', 'name': 'Mimi Rogers', 'toDate': '1990-02-04'}], 'trademarks': ['Often plays romantic leading men with an edge', 'Often plays characters caught up in extraordinary circumstances', 'Frequently plays intelligent yet laidback and likeable characters', 'Beaming smile and intense eye contact', 'Boundless off-stage energy', 'Many of his films feature scenes of him running', 'Known for doing many of his own stunts, even exceptionally dangerous ones'], 'introduction': 'In 1976, if you had told fourteen-year-old Franciscan seminary student Thomas Cruise Mapother IV that one day in the not too distant future he would be Tom Cruise, one of the top 100 movie stars of all time, he would have probably grinned and told you that his ambition was to join the priesthood. Nonetheless, this sensitive, deeply religious youngster who was born in 1962 in Syracuse, New York, was destined to become one of the highest paid and most sought after actors in screen history. ...', 'full_intruduction': "In 1976, if you had told fourteen-year-old Franciscan seminary student Thomas Cruise Mapother IV that one day in the not too distant future he would be Tom Cruise, one of the top 100 movie stars of all time, he would have probably grinned and told you that his ambition was to join the priesthood. Nonetheless, this sensitive, deeply religious youngster who was born in 1962 in Syracuse, New York, was destined to become one of the highest paid and most sought after actors in screen history.\n\nTom is the only son (among four children) of nomadic parents, Mary Lee (Pfeiffer), a special education teacher, and Thomas Cruise Mapother III, an electrical engineer. His parents were both from Louisville, Kentucky, and he has German, Irish, and English ancestry. Young Tom spent his boyhood always on the move, and by the time he was 14 he had attended 15 different schools in the U.S. and Canada. He finally settled in Glen Ridge, New Jersey with his mother and her new husband. While in high school, Tom wanted to become a priest but pretty soon he developed an interest in acting and abandoned his plans of becoming a priest, dropped out of school, and at age 18 headed for New York and a possible acting career. The next 15 years of his life are the stuff of legends. He made his film debut with a small part in Endless Love (1981) and from the outset exhibited an undeniable box office appeal to both male and female audiences.\n\nWith handsome movie star looks and a charismatic smile, within 5 years Tom Cruise was starring in some of the top-grossing films of the 1980s including Top Gun (1986); The Color of Money (1986), Rain Man (1988) and Born on the Fourth of July (1989). By the 1990s he was one of the highest-paid actors in the world earning an average 15 million dollars a picture in such blockbuster hits as Interview with the Vampire: The Vampire Chronicles (1994), Mission: Impossible (1996) and Jerry Maguire (1996), for which he received an Academy Award Nomination for best actor. Tom Cruise's biggest franchise, Mission Impossible, has also earned a total of 3 billion dollars worldwide. Tom Cruise has also shown lots of interest in producing, with his biggest producer credits being the Mission Impossible franchise.\n\nIn 1990 he renounced his devout Catholic beliefs and embraced The Church of Scientology claiming that Scientology teachings had cured him of the dyslexia that had plagued him all of his life. A kind and thoughtful man well known for his compassion and generosity, Tom Cruise is one of the best liked members of the movie community. He was married to actress Nicole Kidman until 2001. Thomas Cruise Mapother IV has indeed come a long way from the lonely wanderings of his youth to become one of the biggest movie stars ever.", 'known_for': [{'title': 'Top Gun', 'image': 'https://m.media-amazon.com/images/M/MV5BZjQxYTA3ODItNzgxMy00N2Y2LWJlZGMtMTRlM2JkZjI1ZDhhXkEyXkFqcGdeQXVyNDk3NzU2MTQ@._V1_.jpg'}, {'title': 'The Last Samurai', 'image': 'https://m.media-amazon.com/images/M/MV5BMzkyNzQ1Mzc0NV5BMl5BanBnXkFtZTcwODg3MzUzMw@@._V1_.jpg'}, {'title': 'Jerry Maguire', 'image': 'https://m.media-amazon.com/images/M/MV5BYTM0ZWNmZTUtOTVkZS00MTZiLTg3M2QtZjA0Y2RmOWM1NWEyXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_.jpg'}, {'title': 'Mission: Impossible II', 'image': 'https://m.media-amazon.com/images/M/MV5BN2RkYWVkZDQtNTMxMi00NWQ4LWE2ODctNmQzOWM2NjQzYzdlXkEyXkFqcGdeQXVyMjUzOTY1NTc@._V1_.jpg'}]}, {'id': 'nm1089991', 'name': 'Tom Hiddleston', 'birthDate': '1981-02-09', 'gender': 'male', 'height': 187.3, 'fullName': 'Thomas William Hiddleston', 'image': 'https://m.media-amazon.com/images/M/MV5BNWYwODAyZjAtOTQ1My00MDY2LTg0NDQtZGFiMDRiYzY4ZmM2XkEyXkFqcGdeQXVyNjg2NjQwMDQ@._V1_.jpg', 'akas': None, 'nicknames': ['Hiddles'], 'birthPlace': 'Westminster, London, England, UK', 'spouses': None, 'trademarks': ['Self-described blond curly "Gene Wilder" hair.', 'His wide, warm smile', 'Expressive blue-green eyes', 'Deep smooth voice', 'Extremely polite, articulate and friendly personality'], 'introduction': 'Thomas William Hiddleston was born in Westminster, London, to English-born Diana Patricia (Servaes) and Scottish-born James Norman Hiddleston. His mother is a former stage manager, and his father, a scientist, was the managing director of a pharmaceutical company. He started off at the preparatory school, The Dragon School in Oxford, and by the time he was 13, he boarded at Eton College, at the same time that his parents were going through a divorce. He continued on to the University of Cambridge, where he earned a double first in Classics. He continued to study acting at the Royal Academy of Dramatic Art, from which he graduated in 2005. ...', 'full_intruduction': 'Thomas William Hiddleston was born in Westminster, London, to English-born Diana Patricia (Servaes) and Scottish-born James Norman Hiddleston. His mother is a former stage manager, and his father, a scientist, was the managing director of a pharmaceutical company. He started off at the preparatory school, The Dragon School in Oxford, and by the time he was 13, he boarded at Eton College, at the same time that his parents were going through a divorce. He continued on to the University of Cambridge, where he earned a double first in Classics. He continued to study acting at the Royal Academy of Dramatic Art, from which he graduated in 2005.\n\nWhilst at University of Cambridge, he was seen by the agency \'Hamilton Hodell\' in the play "A Streetcar Named Desire" and was signed. Following this, he was cast in his first television role in The Life and Adventures of Nicholas Nickleby (2001). Hiddleston won his first film role as Oakley in Joanna Hogg\'s award-winning first feature, Unrelated (2007). His breakthrough role came when he portrayed the nemesis Loki in the Marvel Cinematic Universe feature film Thor (2011). He reprised the character in The Avengers (2012), Thor: The Dark World (2013), Thor: Ragnarok (2017) and Avengers: Infinity War (2018).\n\nHe has also appeared in Steven Spielberg\'s War Horse (2011), The Deep Blue Sea (2011), Woody Allen\'s romantic comedy Midnight in Paris (2011), and the romantic vampire film Only Lovers Left Alive (2013). On television, he appeared on the BBC series The Hollow Crown (2012), in the adaptations of Shakespeare\'s "Henry IV" and "Henry V". In theatre, he has been in the productions of "Cymbeline" (2007) and "Ivanov" (2008). In December 2013, he starred as the title character in the Donmar Warehouse production of "Coriolanus" which played until February 2014. He won the Laurence Olivier Award for Best Newcomer in a Play for his role in "Cymbeline" while also being nominated for the same award the same year for his role as Cassio in "Othello".', 'known_for': [{'title': 'The Avengers', 'image': 'https://m.media-amazon.com/images/M/MV5BNDYxNjQyMjAtNTdiOS00NGYwLWFmNTAtNThmYjU5ZGI2YTI1XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_.jpg'}, {'title': 'Thor', 'image': 'https://m.media-amazon.com/images/M/MV5BOGE4NzU1YTAtNzA3Mi00ZTA2LTg2YmYtMDJmMThiMjlkYjg2XkEyXkFqcGdeQXVyNTgzMDMzMTg@._V1_.jpg'}, {'title': 'Thor: The Dark World', 'image': 'https://m.media-amazon.com/images/M/MV5BMTQyNzAwOTUxOF5BMl5BanBnXkFtZTcwMTE0OTc5OQ@@._V1_.jpg'}, {'title': 'Thor: Ragnarok', 'image': 'https://m.media-amazon.com/images/M/MV5BMjMyNDkzMzI1OF5BMl5BanBnXkFtZTgwODcxODg5MjI@._V1_.jpg'}]}, {'id': 'nm0255124', 'name': 'Tom Ellis', 'birthDate': '1978-11-17', 'gender': 'male', 'height': 190.5, 'fullName': 'Thomas John Ellis', 'image': 'https://m.media-amazon.com/images/M/MV5BYmY2OGMzM2YtMDVlNi00NzdhLTkzNDMtZjA0M2I2YzNiNDIyXkEyXkFqcGdeQXVyNjc1OTk0NzA@._V1_.jpg', 'akas': None, 'nicknames': None, 'birthPlace': 'Cardiff, Wales, UK', 'spouses': [{'current': True, 'fromDate': '2019-06-02', 'id': '/name/nm0649198/', 'name': 'Meaghan Oppenheimer'}, {'attributes': '(divorced) (2 children)', 'fromDate': '2006-06-11', 'id': '/name/nm0653666/', 'name': 'Tamzin Outhwaite', 'toDate': '2014-04-28'}], 'trademarks': ['Towering height'], 'introduction': 'Tom Ellis is a Welsh actor from Cardiff, Wales. He is best known for playing Lucifer Morningstar in the American television series Lucifer (2016-2020). ...', 'full_intruduction': "Tom Ellis is a Welsh actor from Cardiff, Wales. He is best known for playing Lucifer Morningstar in the American television series Lucifer (2016-2020).\n\nEllis was born in Cardiff. He studied BA Dramatic Studies at the Royal Conservatoire of Scotland (previously the Royal Scottish Academy of Music and Drama).\n\nEllis' notable roles include Hollywood physician in the USA Network series Rush, Mark Etches in the British supernatural drama The Fades and Gary Preston in the hit TV show Miranda which aired on the BBC from November 9, 2009 to January 1, 2015.\n\nIn February 2015, it was announced that Ellis was cast as Lucifer Morningstar in the Fox television drama Lucifer, based on the comic of the same name, which premiered on 25 January 2016. The show was renewed by Netflix for a fourth season which was released on 8 May 2019.", 'known_for': [{'title': 'Lucifer', 'image': 'https://m.media-amazon.com/images/M/MV5BNDJjMzc4NGYtZmFmNS00YWY3LThjMzQtYzJlNGFkZGRiOWI1XkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_.jpg'}, {'title': "Isn't It Romantic", 'image': 'https://m.media-amazon.com/images/M/MV5BZGE1NGYxOWItODdmMy00NWNhLTgxZmMtYmVjYmViMGI0NTdmXkEyXkFqcGdeQXVyNzE2MTQyMzM@._V1_.jpg'}, {'title': 'Miranda', 'image': 'https://m.media-amazon.com/images/M/MV5BOTQyMzA1Nzg4Ml5BMl5BanBnXkFtZTcwNTQxMTg4Mw@@._V1_.jpg'}, {'title': 'Buffalo Soldiers', 'image': 'https://m.media-amazon.com/images/M/MV5BMTI1MDYxMTE3MV5BMl5BanBnXkFtZTYwMzc1NDk5._V1_.jpg'}]}, {'id': 'nm2584392', 'name': 'Tom Hopper', 'birthDate': '1985-01-28', 'gender': 'male', 'height': 193.04, 'fullName': None, 'image': 'https://m.media-amazon.com/images/M/MV5BN2Y1OGZjNWUtYTdmMy00OGQxLTg5MmYtOTRjMjMxZWI1NTJhXkEyXkFqcGdeQXVyMjQwMDg0Ng@@._V1_.jpg', 'akas': None, 'nicknames': None, 'birthPlace': 'Coalville, Leicestershire, England, UK', 'spouses': [{'attributes': '(2 children)', 'current': True, 'fromDate': '2014-06-07', 'id': '/name/nm1365578/', 'name': 'Laura Hopper'}], 'trademarks': ['Towering height', 'Muscular physique'], 'introduction': 'English-born Tom Hopper has come a long way in his acting career. A former student of Rose Bruford College where he studied acting, Tom Hopper has become widely known for his involvement in both films and in some of television\'s highly popular drama series including Merlin (2008) as the strongman knight Sir Percival, followed with a memorable performance (almost appearing as the spitting image of Marvel Comic\'s "Thor" played by Chris Hemsworth) in the medieval swashbuckler Northmen - A Viking Saga (2014). Then Hopper joined a two-part mini-series Barbarians Rising (2016) as the Roman soldier Arminius, before signing on with the Starz drama series Black Sails (2014). Without losing any demand for his 6\'5", musculature presence, HBO\'s medieval fantasy drama series Game of Thrones (2011) recruited him for its eighth season to portray the swordsman-hunter Dickon Tarly. ...', 'full_intruduction': 'English-born Tom Hopper has come a long way in his acting career. A former student of Rose Bruford College where he studied acting, Tom Hopper has become widely known for his involvement in both films and in some of television\'s highly popular drama series including Merlin (2008) as the strongman knight Sir Percival, followed with a memorable performance (almost appearing as the spitting image of Marvel Comic\'s "Thor" played by Chris Hemsworth) in the medieval swashbuckler Northmen - A Viking Saga (2014). Then Hopper joined a two-part mini-series Barbarians Rising (2016) as the Roman soldier Arminius, before signing on with the Starz drama series Black Sails (2014). Without losing any demand for his 6\'5", musculature presence, HBO\'s medieval fantasy drama series Game of Thrones (2011) recruited him for its eighth season to portray the swordsman-hunter Dickon Tarly.', 'known_for': [{'title': 'Black Sails', 'image': 'https://m.media-amazon.com/images/M/MV5BZmExNzU1M2YtNGUzOC00MmM0LWEwYzYtZWJiYjYxNDcwY2E0XkEyXkFqcGdeQXVyMTYzMDM0NTU@._V1_.jpg'}, {'title': 'Game of Thrones', 'image': 'https://m.media-amazon.com/images/M/MV5BYTRiNDQwYzAtMzVlZS00NTI5LWJjYjUtMzkwNTUzMWMxZTllXkEyXkFqcGdeQXVyNDIzMzcwNjc@._V1_.jpg'}, {'title': 'Northmen - A Viking Saga', 'image': 'https://m.media-amazon.com/images/M/MV5BNTI0ZjljMzMtNDFlZi00YTM1LTgxY2EtN2Q1ZjNiZTNkMzQ3L2ltYWdlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_.jpg'}, {'title': 'Merlin', 'image': 'https://m.media-amazon.com/images/M/MV5BZTBjYjM3ZjItZTI1MS00ODZhLWFhZDgtODgxMmMzN2JlOTExXkEyXkFqcGdeQXVyNTA4NzY1MzY@._V1_.jpg'}]}]

        row = 1
        for actor in self.controller.values["result"]:
            if actor["image"] == None:
                tempImg = tk.Label(self.content, text="No Image", font=("Calibri italic", 25))
            else:
                image = self.urltoImg(actor["image"], 200, 300)
                tempImg = tk.Label(self.content, image=image)
                tempImg.image = image
            tempImg.grid(row=row, column=0)
            temp = tk.Button(self.content, text=f'{actor["name"]}', font=("Calibri italic", 25),
                             command=partial(self.actor, row-1))
            temp.grid(row=row, column=1)
            row += 1

    def actor(self, row):
        self.frame.destroy()
        self.controller.values["actor"] = self.controller.values["result"][row]
        self.controller.show_frame("ActorPage")

    def home(self):
        self.frame.destroy()
        self.controller.show_frame("HomePage")

    def search(self):
        self.frame.destroy()
        function = self.controller.values["function"].get()
        if function == 1:
            self.controller.show_frame("ActorSearchPage")
        elif function == 2:
            self.controller.show_frame("FilmSearchPage")
        elif function == 3:
            self.controller.show_frame("CollabSearchPage")